'use strict'

var gitHosts = module.exports = {
  github: {
    // First two are insecure and generally shouldn't be used any more, but
    // they are still supported.
    'protocols': [ 'git', 'http', 'git+ssh', 'git+https', 'ssh', 'https' ],
    'domain': 'github.com',
    'treepath': 'tree',
    'filetemplate': 'https://{auth@}raw.githubusercontent.com/{user}/{app}/{committish}/{path}',
    'bugstemplate': 'https://{domain}/{user}/{app}/issues',
    'gittemplate': 'git://{auth@}{domain}/{user}/{app}.git{#committish}'
  },
  bitbucket: {
    'protocols': [ 'git+ssh', 'git+https', 'ssh', 'https' ],
    'domain': 'bitbucket.org',
    'treepath': 'src'
  },
  gitlab: {
    'protocols': [ 'git+ssh', 'git+https', 'ssh', 'https' ],
    'domain': 'gitlab.com',
    'treepath': 'tree',
    'docstemplate': 'https://{domain}/{user}/{app}{/tree/committish}#README',
    'bugstemplate': 'https://{domain}/{user}/{app}/issues'
  },
  gist: {
    'protocols': [ 'git', 'git+ssh', 'git+https', 'ssh', 'https' ],
    'domain': 'gist.github.com',
    'pathmatch': /^[/](?:([^/]+)[/])?([a-z0-9]+)(?:[.]git)?$/,
    'filetemplate': 'https://gist.githubusercontent.com/{user}/{app}/raw{/committish}/{path}',
    'bugstemplate': 'https://{domain}/{app}',
    'gittemplate': 'git://{domain}/{app}.git{#committish}',
    'sshtemplate': 'git@{domain}:/{app}.git{#committish}',
    'sshurltemplate': 'git+ssh://git@{domain}/{app}.git{#committish}',
    'browsetemplate': 'https://{domain}/{app}{/committish}',
    'docstemplate': 'https://{domain}/{app}{/committish}',
    'httpstemplate': 'git+https://{domain}/{app}.git{#committish}',
    'shortcuttemplate': '{type}:{app}{#committish}',
    'pathtemplate': '{app}{#committish}'
  }
}

var gitHostDefaults = {
  'sshtemplate': 'git@{domain}:{user}/{app}.git{#committish}',
  'sshurltemplate': 'git+ssh://git@{domain}/{user}/{app}.git{#committish}',
  'browsetemplate': 'https://{domain}/{user}/{app}{/tree/committish}',
  'docstemplate': 'https://{domain}/{user}/{app}{/tree/committish}#readme',
  'httpstemplate': 'git+https://{auth@}{domain}/{user}/{app}.git{#committish}',
  'filetemplate': 'https://{domain}/{user}/{app}/raw/{committish}/{path}',
  'shortcuttemplate': '{type}:{user}/{app}{#committish}',
  'pathtemplate': '{user}/{app}{#committish}',
  'pathmatch': /^[/]([^/]+)[/]([^/]+?)(?:[.]git)?$/
}

Object.keys(gitHosts).forEach(function (name) {
  Object.keys(gitHostDefaults).forEach(function (key) {
    if (gitHosts[name][key]) return
    gitHosts[name][key] = gitHostDefaults[key]
  })
  gitHosts[name].protocols_re = RegExp('^(' +
    gitHosts[name].protocols.map(function (protocol) {
      return protocol.replace(/([\\+*{}()\[\]$^|])/g, '\\$1')
    }).join('|') + '):$')
})
